UPDATE container SET payload['type'] = to_jsonb('operational_goal'::text) WHERE payload->>'type' = 'milestone' AND guid IN (SELECT m.guid FROM container AS m JOIN container AS o ON o.guid = m.organization AND o.valid_currently JOIN container_relation ON subject = m.revision AND predicate = 'is-part-of-strategy' AND m.valid_currently JOIN container s ON s.revision = object AND s.valid_currently WHERE m.payload->>'type' = 'milestone' ORDER BY m.revision)
